options.css:
/* Minimalist Design System - Clean and Fast */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

.dashboard-container {
    background: #ffffff;
    color: #000000;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    padding: 1.5rem;
    min-height: 100vh;
}

/* Breadcrumb Navigation */
.breadcrumb {
    font-size: 0.875rem;
    margin-bottom: 1rem;
    color: #666666;
}

.breadcrumb a {
    color: #000000;
    text-decoration: underline;
}

.breadcrumb a:hover {
    opacity: 0.7;
}

.separator {
    margin: 0 0.5rem;
    color: #cccccc;
}

.breadcrumb .current {
    color: #000000;
    font-weight: 500;
}

/* Header section - minimal styling */
.header {
    border-bottom: 1px solid #cccccc;
    padding-bottom: 1rem;
    margin-bottom: 2rem;
}

.title {
    font-size: 1.875rem;
    font-weight: 700;
    color: #000000;
    margin: 0;
}

.subtitle {
    font-size: 0.875rem;
    color: #666666;
    margin-top: 0.25rem;
}

/* Stats cards - minimal borders only */
.stats-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.stat-card {
    background: #ffffff;
    border: 1px solid #cccccc;
    padding: 1.5rem;
}

.stat-label {
    font-size: 0.875rem;
    font-weight: 700;
    color: #000000;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.stat-value {
    font-size: 1.875rem;
    font-weight: 700;
    color: #000000;
    margin-top: 0.5rem;
}

/* Data table - clean and minimal */
.table-container {
    background: #ffffff;
    border: 1px solid #cccccc;
    overflow: hidden;
}

.data-table {
    width: 100%;
    border-collapse: collapse;
}

.data-table thead {
    background: #f7f7f7;
}

.data-table th {
    text-align: left;
    padding: 0.75rem 1.5rem;
    font-size: 0.75rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: #000000;
    border-bottom: 1px solid #cccccc;
}

.data-table td {
    padding: 1rem 1.5rem;
    font-size: 0.875rem;
    border-bottom: 1px solid #cccccc;
    color: #000000;
}

.data-table tbody tr:last-child td {
    border-bottom: none;
}

.data-table tbody tr:hover {
    background: #f7f7f7;
}

.product-name {
    font-weight: 500;
    color: #000000;
}

.product-name-link {
    color: #000000;
    text-decoration: underline;
    font-weight: 500;
    cursor: pointer;
}

.product-name-link:hover {
    opacity: 0.7;
}

.metric-value {
    color: #666666;
}

.action-links {
    display: flex;
    gap: 1rem;
}

.action-link {
    color: #000000;
    text-decoration: underline;
    font-weight: 500;
}

.action-link:hover {
    opacity: 0.7;
}

/* Status colors - ONLY place with color */
.health-good {
    color: #00AA00;
}

.health-warning {
    color: #FFAA00;
}

.health-critical {
    color: #FF0000;
}

/* Business purpose text truncation */
.business-purpose {
    max-width: 400px;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

/* Loading state */
.loading {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 400px;
    color: #666666;
}

.error {
    color: #FF0000;
    padding: 1rem;
    text-align: center;
}

options.onRender:
// Helper functions
const getHealthClass = (value) => {
    if (value >= 95) return 'health-good';
    if (value >= 90) return 'health-warning';
    return 'health-critical';
};

// Process data
try {
    // Get DOM elements
    const tableBody = htmlNode.querySelector('#tableBody');
    const coverageStat = htmlNode.querySelector('#coverageStat');
    const healthStat = htmlNode.querySelector('#healthStat');
    const incidentsStat = htmlNode.querySelector('#incidentsStat');

    if (!tableBody) {
        console.error('tableBody element not found');
        return;
    }

    // Check if data is available
    let queryData = null;

    if (typeof data !== 'undefined' && data && data.series) {
        queryData = data.series;
    } else if (typeof htmlGraphics !== 'undefined' && htmlGraphics.data && htmlGraphics.data.series) {
        queryData = htmlGraphics.data.series;
    }

    if (!queryData || queryData.length === 0) {
        tableBody.innerHTML = '<tr><td colspan="4" class="error">No data available</td></tr>';
        return;
    }

    const series = queryData[0];
    const fields = series.fields;

    if (!fields || fields.length === 0) {
        tableBody.innerHTML = '<tr><td colspan="4" class="error">No fields in data</td></tr>';
        return;
    }

    // Find field indices
    const fieldMap = {};
    fields.forEach((field, index) => {
        fieldMap[field.name] = index;
    });

    // Get values arrays
    const values = fields.map(f => f.values.buffer || f.values || []);
    const rowCount = values[0] ? values[0].length : 0;

    if (rowCount === 0) {
        tableBody.innerHTML = '<tr><td colspan="4" class="error">No rows in data</td></tr>';
        return;
    }

    // Calculate summary stats (worst wins)
    let minHealth = 100;
    let minHealthStatus = 'Green';
    let totalIncidents = 0;
    let totalServicesWithSignals = 0;
    let totalServices = 0;

    // Build table rows
    let tableHTML = '';

    for (let i = 0; i < rowCount; i++) {
        const entityName = values[fieldMap['l4_product_line']] ? values[fieldMap['l4_product_line']][i] : 'Unknown';
        const businessPurpose = values[fieldMap['business_purpose']] ? values[fieldMap['business_purpose']][i] : '';
        const serviceCount = values[fieldMap['service_count']] ? values[fieldMap['service_count']][i] : 0;
        const servicesWithSignals = values[fieldMap['services_with_signals']] ? values[fieldMap['services_with_signals']][i] : 0;
        const coverage = values[fieldMap['coverage']] ? values[fieldMap['coverage']][i] : 0;
        let health = values[fieldMap['health']] ? values[fieldMap['health']][i] : null;
        const healthStatus = values[fieldMap['health_status']] ? values[fieldMap['health_status']][i] : 'Unknown';
        const incidentCount = values[fieldMap['incident_count']] ? values[fieldMap['incident_count']][i] : 0;

        // Handle null health values
        if (health === null || health === undefined) {
            health = 0;
        }

        // Update totals - worst wins
        if (health !== null && health < minHealth) minHealth = health;
        if (healthStatus === 'Red' || minHealthStatus === 'Red') {
            minHealthStatus = 'Red';
        } else if (healthStatus === 'Amber' && minHealthStatus !== 'Red') {
            minHealthStatus = 'Amber';
        }
        totalIncidents += incidentCount || 0;
        totalServicesWithSignals += servicesWithSignals || 0;
        totalServices += serviceCount || 0;

        // Status class for display
        const statusClass = healthStatus === 'Green' ? 'health-good' :
                           healthStatus === 'Amber' ? 'health-warning' :
                           'health-critical';

        // Conditionally include business purpose cell
        const hasBusinessPurposeField = 'business_purpose' in fieldMap;
        const businessPurposeCell = hasBusinessPurposeField ?
            `<td class="business-purpose" title="${businessPurpose || ''}">${businessPurpose || ''}</td>` : '';

        // Build row HTML
        tableHTML += `
            <tr>
                <td>
                    <a href="/d/exec-l3-minimalist-v2/l3-products-minimalist-v2?var-l4_product_line=${encodeURIComponent(entityName)}"
                       class="product-name-link">
                        ${entityName}
                    </a>
                </td>
                ${businessPurposeCell}
                <td class="metric-value">${servicesWithSignals}/${serviceCount} (${coverage.toFixed(1)}%)</td>
                <td class="metric-value ${statusClass}">${(health || 0).toFixed(1)}% (${healthStatus})</td>
                <td class="metric-value">${incidentCount}</td>
            </tr>
        `;
    }

    // Update table
    tableBody.innerHTML = tableHTML;

    // Update summary stats
    const worstHealth = minHealth === 100 ? 0 : minHealth;
    const worstStatusClass = minHealthStatus === 'Green' ? 'health-good' :
                            minHealthStatus === 'Amber' ? 'health-warning' :
                            'health-critical';

    const platformCoverage = totalServices > 0 ?
        ((totalServicesWithSignals / totalServices) * 100).toFixed(1) : 0;

    if (coverageStat) {
        coverageStat.textContent = totalServicesWithSignals + '/' + totalServices + ' (' + platformCoverage + '%)';
        coverageStat.className = 'stat-value';
    }

    if (healthStat) {
        healthStat.textContent = worstHealth.toFixed(1) + '% (' + minHealthStatus + ')';
        healthStat.className = 'stat-value ' + worstStatusClass;
    }

    if (incidentsStat) {
        incidentsStat.textContent = totalIncidents;
    }

} catch (err) {
    console.error('Error processing data:', err);
    const tableBody = htmlNode.querySelector('#tableBody');
    if (tableBody) {
        tableBody.innerHTML = '<tr><td colspan="4" class="error">Error: ' + err.message + '</td></tr>';
    }
}
